import React from 'react';

const ToggleSwitch = ({ checked, onChange, label }) => {
  return (
    <label className="flex items-center gap-4 cursor-pointer text-lg font-semibold text-pink-cute">
      <div className="relative">
        <input
          type="checkbox"
          checked={checked}
          onChange={(e) => onChange(e.target.checked)}
          className="sr-only"
        />
        <div className={`w-16 h-8 rounded-full transition-all duration-300 ${
          checked ? 'bg-pink-cute shadow-lg shadow-pink-cute/40' : 'bg-pink-200'
        }`}>
          <div className={`w-6 h-6 bg-white rounded-full shadow-md transition-all duration-300 transform ${
            checked ? 'translate-x-8 scale-110' : 'translate-x-1'
          } mt-1`}></div>
        </div>
      </div>
      <span className="select-none">{label}</span>
    </label>
  );
};

export default ToggleSwitch;
