import React, { useState } from 'react';
import { Input, Button } from '../ui';
import AngerSlider from './AngerSlider';

const GrievanceForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    complaint: '',
    angerLevel: 5,
    solution: '',
    hugAnyway: false,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);

    // Reset form
    setFormData({
      complaint: '',
      angerLevel: 5,
      solution: '',
      hugAnyway: false,
    });
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <div className="bg-white/90 backdrop-blur-lg rounded-3xl p-8 md:p-12 shadow-2xl border-2 border-pink-200/50">
      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Complaint Field */}
        <Input
          label="batao toh maine kya kiya"
          type="textarea"
          value={formData.complaint}
          onChange={(e) => handleInputChange('complaint', e.target.value)}
          placeholder="sowwi â˜•"
          required
          rows={5}
        />

        {/* Anger Level Slider */}
        <div className="space-y-3">
          <label className="block text-lg font-semibold text-pink-cute">
            ðŸ˜¤ gussa kitna hai?
          </label>
          <AngerSlider
            value={formData.angerLevel}
            onChange={(value) => handleInputChange('angerLevel', value)}
          />
        </div>

        {/* Solution Field */}
        <Input
          label="ðŸ’¡ kaise theek karu batao"
          type="textarea"
          value={formData.solution}
          onChange={(e) => handleInputChange('solution', e.target.value)}
          placeholder="saari baatein maanunga"
          rows={4}
        />

        {/* Hug Toggle */}
        <div className="flex items-center justify-center">
          <ToggleSwitch
            checked={formData.hugAnyway}
            onChange={(checked) => handleInputChange('hugAnyway', checked)}
            label="kissie?"
          />
        </div>

        {/* Submit Button */}
        <div className="flex justify-center">
          <Button type="submit" variant="primary">
            <span className="text-2xl animate-wiggle">ðŸ§¸</span>
            <span>Submit Complaint</span>
          </Button>
        </div>

      </form>
    </div>
  );
};

export default GrievanceForm;
