import React, { useState } from 'react';

const ApologyButton = () => {
  const [isPlaying, setIsPlaying] = useState(false);

  const playApologySong = () => {
    if (typeof AudioContext !== "undefined" || typeof webkitAudioContext !== "undefined") {
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();

      // Create a simple melody
      const notes = [523.25, 587.33, 659.25, 698.46, 783.99]; // C, D, E, F, G

      notes.forEach((freq, index) => {
        const oscillator = audioContext.createOscillator();
        const gainNode = audioContext.createGain();

        oscillator.connect(gainNode);
        gainNode.connect(audioContext.destination);

        oscillator.frequency.setValueAtTime(freq, audioContext.currentTime + index * 0.3);
        oscillator.type = 'sine';

        gainNode.gain.setValueAtTime(0.1, audioContext.currentTime + index * 0.3);
        gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + index * 0.3 + 0.25);

        oscillator.start(audioContext.currentTime + index * 0.3);
        oscillator.stop(audioContext.currentTime + index * 0.3 + 0.25);
      });
    }

    setIsPlaying(true);
    setTimeout(() => {
      setIsPlaying(false);
    }, 2000);
  };

  return (
    <button
      type="button"
      onClick={playApologySong}
      className="bg-gradient-to-r from-purple-500 to-purple-700 border-none rounded-3xl px-8 py-4 font-quicksand text-lg font-semibold text-white cursor-pointer transition-all duration-300 shadow-lg shadow-purple-500/40 hover:-translate-y-1 hover:scale-105 hover:shadow-xl hover:shadow-purple-500/60 active:scale-95"
    >
      {isPlaying ? 'ðŸŽµ Playing... ðŸŽµ' : 'ðŸŽµ Play Apology Song'}
    </button>
  );
};

export default ApologyButton;
