import React from 'react';

const AngerSlider = ({ value, onChange }) => {
  const angerLevels = [
    { emoji: 'üòä', text: 'Actually happy' },
    { emoji: 'üòê', text: 'Slightly bothered' },
    { emoji: 'üòï', text: 'A bit annoyed' },
    { emoji: 'üòí', text: 'Mildly irritated' },
    { emoji: 'üò†', text: 'Getting mad' },
    { emoji: 'üò°', text: 'Pretty angry' },
    { emoji: 'ü§¨', text: 'Very upset' },
    { emoji: 'üò§', text: 'Fuming mad' },
    { emoji: 'üëø', text: 'Absolutely livid' },
    { emoji: 'üíÄ', text: "You're dead to me" }
  ];

  const currentLevel = angerLevels[value - 1];

  return (
    <div className="relative p-6 bg-pink-50/60 rounded-2xl border-2 border-pink-200/40 backdrop-blur-sm">
      <div className="space-y-6">
        {/* Slider */}
        <div className="relative">
          <input
            type="range"
            min="1"
            max="10"
            value={value}
            onChange={(e) => onChange(parseInt(e.target.value))}
            className="w-full h-3 rounded-full appearance-none cursor-pointer slider-gradient border-2 border-white/80 shadow-lg shadow-pink-cute/30 hover:scale-[1.02] transition-all duration-300"
            style={{
              background: 'linear-gradient(to right, #98fb98 0%, #87ceeb 20%, #dda0dd 40%, #ffa07a 60%, #ff6347 80%, #dc143c 100%)',
            }}
          />
          <style jsx>{`
            input[type="range"]::-webkit-slider-thumb {
              -webkit-appearance: none;
              appearance: none;
              width: 35px;
              height: 35px;
              border-radius: 50%;
              background: linear-gradient(135deg, #ff69b4, #ff1493);
              cursor: pointer;
              border: 4px solid white;
              box-shadow: 0 4px 15px rgba(255, 20, 147, 0.4), 0 2px 8px rgba(0, 0, 0, 0.2);
              transition: all 0.2s ease;
            }

            input[type="range"]::-webkit-slider-thumb:hover {
              transform: scale(1.15);
              box-shadow: 0 6px 20px rgba(255, 20, 147, 0.6), 0 4px 12px rgba(0, 0, 0, 0.3);
            }

            input[type="range"]::-moz-range-thumb {
              width: 35px;
              height: 35px;
              border-radius: 50%;
              background: linear-gradient(135deg, #ff69b4, #ff1493);
              cursor: pointer;
              border: 4px solid white;
              box-shadow: 0 4px 15px rgba(255, 20, 147, 0.4), 0 2px 8px rgba(0, 0, 0, 0.2);
            }
          `}</style>
        </div>

        {/* Display */}
        <div className="text-center bg-white/80 p-4 rounded-2xl border-2 border-pink-cute/20 shadow-lg shadow-pink-cute/10">
          <div className="text-5xl animate-pulse-cute drop-shadow-sm mb-2">
            {currentLevel.emoji}
          </div>
          <div className="font-bold text-purple-cute text-lg uppercase tracking-wide drop-shadow-sm">
            {currentLevel.text}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AngerSlider;
