import React, { useState } from 'react';
import { Input, Button } from '../ui';
import AngerSlider from './AngerSlider';
import ToggleSwitch from './ToggleSwitch';
import ApologyButton from '../feedback/ApologyButton';

const GrievanceForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    complaint: '',
    angerLevel: 5,
    solution: '',
    hugAnyway: false,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);

    // Reset form
    setFormData({
      complaint: '',
      angerLevel: 5,
      solution: '',
      hugAnyway: false,
    });
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <div className="bg-white/90 backdrop-blur-lg rounded-3xl p-8 md:p-12 shadow-2xl border-2 border-pink-200/50">
      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Complaint Field */}
        <Input
          label="ðŸ’­ What did he do now?"
          type="textarea"
          value={formData.complaint}
          onChange={(e) => handleInputChange('complaint', e.target.value)}
          placeholder="Spill the tea, sweetie... â˜•"
          required
          rows={5}
        />

        {/* Anger Level Slider */}
        <div className="space-y-3">
          <label className="block text-lg font-semibold text-pink-cute">
            ðŸ˜¤ How mad are you?
          </label>
          <AngerSlider
            value={formData.angerLevel}
            onChange={(value) => handleInputChange('angerLevel', value)}
          />
        </div>

        {/* Solution Field */}
        <Input
          label="ðŸ’¡ What can he do to fix it?"
          type="textarea"
          value={formData.solution}
          onChange={(e) => handleInputChange('solution', e.target.value)}
          placeholder="Your wish is my command... ðŸ§žâ€â™‚ï¸"
          rows={4}
        />

        {/* Hug Toggle */}
        <div className="flex items-center justify-center">
          <ToggleSwitch
            checked={formData.hugAnyway}
            onChange={(checked) => handleInputChange('hugAnyway', checked)}
            label="ðŸ¤— Hug me anyway?"
          />
        </div>

        {/* Buttons */}
        <div className="flex flex-col sm:flex-row gap-5 justify-center items-center">
          <Button type="submit" variant="primary">
            <span className="text-2xl animate-wiggle">ðŸ§¸</span>
            <span>Submit Complaint</span>
          </Button>

          <ApologyButton />
        </div>
      </form>
    </div>
  );
};

export default GrievanceForm;
